import React from 'react';
import './header.css';
import {Container, Row, Col} from 'react-bootstrap';
import Wave from 'react-wavify'
import Button from '../../common/button/button.js';

function header() {
    return (
        <>
        <header className = "header d-flex align-items-end justify-content-center" id = "home">
            <Container fluid>
                <Row className = "mt-5">
                    <Col data-aos = "fade-right" className = "mt-5 d-flex justify-content-center">
                        <h1 className = "text-center" >A new way to connect with people</h1>
                    </Col>
                </Row> 
                <Button effectType = "fade-up" link = "/#about" contentText = "learn more"></Button>  
            </Container>  
        </header>
        <Container className = "wave-container px-0" fluid>
            <Wave className = "wave" fill='#00B0F0' options={{height: 60, amplitude: 15, speed: 0.20, points: 3}}></Wave>
            <Wave className = "wave" fill='#21242C' options={{height: 100, amplitude: 20, speed: 0.15, points: 5}}></Wave>
        </Container>
        </>
    )
}

export default header
