import React from 'react';
import './button.css';
import {Row, Col} from 'react-bootstrap';

function button({link, contentText, styleType, effectType, clickEffect}) {
    return (
        <div data-aos = {effectType} className = "button">
            <Row>
                <Col className = "d-flex justify-content-center">
                    <a onClick = {() => setLink("https://hubs.mozilla.com/VN3sXzo?embed_token=e3e498051d58f87815b2c6d095ac24fd")} href = {link} className = {`btn btn-xl d-block mx-auto ${styleType || ""}`}>{contentText}</a>
                </Col>
            </Row>
        </div>  
    )
}

export default button
