import React, {useState} from 'react';
import './login.css';
import '../common/style/global.css';
import {UserCheck} from 'react-feather'
import {useForm} from 'react-hook-form';
import axios from 'axios';
import {Container, Row, Col, Button} from 'react-bootstrap';
import Navigation from '../common/navbar/navbar.js';
import Footer from '../common/footer/footer.js';

function Login() {

    const [error, setError] = useState();
    const {register, handleSubmit} = useForm();
    const onSubmit = (data) =>{
        axios({
            method: "POST",
            url: "login/auth",
            data: data
        })
        .then((response) =>{
            if(response.data === 200){
                document.cookie = `Bearer=${response.data.access_token}`;
                window.location = "/support";
            }
        })
        .catch((error) =>{
            if(error.response.status === 401){
                setStatus("You have entered an incorrect password")
            }
            if(error.response.status === 404){
                setStatus("It appears you don't have an account with us, try registering")
            } 
        })

    }


    return (
        <>  
            <Navigation></Navigation>
            <section className = "login">
                <Container className = "h-100 d-flex align-items-center justify-content-center">
                    <Row className = "login-row">
                        <Col>
                            <form data-aos = "fade-down" className = "form-box" onSubmit = {handleSubmit(onSubmit)}>
                                <UserCheck className = "icon mx-auto d-flex justify-content-center" size = {100}></UserCheck>
                                <h3 className = "header-text text-center">login</h3>
                                <Row>
                                    <Col>
                                        <label>Username</label>
                                    </Col>
                                </Row>
                                <Row className = "pb-3">
                                    <Col>
                                        <input {...register("username")} className = "rounded w-100" type = "Username" placeholder = "Username"></input>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <label>Password</label>
                                    </Col>
                                </Row>
                                <Row className = "pb-3">
                                    <Col>
                                        <input {...register("password")} className = "rounded w-100" type = "password" placeholder = "Password"></input>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col className = "d-flex justify-content-center">
                                        <Button className = "button btn-outline-grey" type = "submit">Login</Button>
                                    </Col>
                                </Row>
                            </form>
                        </Col>
                    </Row>
                </Container>
            </section>
            <Footer></Footer>
        </>
    )
}

export default Login
